import React from 'react'
import SectionTitle from '../../components/SectionTitle'
import { projects } from '../../resources/projects';


function Projects() {
   const [selectedItemIndex, setSelectedItemIndex] = React.useState(0);
   return (
    <div>
      <SectionTitle title="Projects" />

      <div className="flex gap-10 py-10 sm:flex-col">
        <div className="flex flex-col w-1/3 gap-10 border-l-2 border-secondary sm:flex-row sm:overflow-x-scroll sm:w-full">
          {projects.map((projects, index) => (
            <div
              onClick={() => {
                setSelectedItemIndex(index);
              }}
              className="cursor-pointer"
            >
              <h1
                className={`text-xl px-5 ${
                  selectedItemIndex === index
                    ? `text-tertiary border-tertiary border-l-4 -ml-[3px] bg-[#313131] py-3`
                    : `text-white `
                } `}
              >
                {projects.title}
              </h1>
            </div>
          ))}
        </div>
        <div className="flex">
          <img src={projects[selectedItemIndex].image} alt="" />
        </div>
        <div className="flex flex-col gap-5">
          <h1 className="text-2xl text-secondary">{projects[selectedItemIndex].title}</h1>
          <h1 className="text-2xl text-tertiary">{projects[selectedItemIndex].image}</h1>
          <h1 className="text-2xl text-white">{projects[selectedItemIndex].description}</h1>
        </div>
      </div>
    </div>
  );
}

export default Projects
